import { useEffect, useState } from 'react';

type UserMeProps = { client: any };

const UserMe: React.FC<UserMeProps> = ({ client }) => {
  const [res, setRes] = useState({});

  useEffect(() => {
    async function init() {
      const response = await client(`
      {
        query { 
          viewer { 
            login
          }
        }
      }
    `);

      setRes(response);
    }

    init();
  }, []);

  return (
    <div>
      Res:
      <br />
      {JSON.stringify(res)}
    </div>
  );
};

export default UserMe;
