import { useEffect, useState } from 'react';
import { request } from '@octokit/request';

import UserMe from '../components/UserMe';
import initializeClient from '../graphql/client';

const ClientProvider: React.FC<any> = ({ code, ...children }) => {
  const [loading, setLoading] = useState(true);
  const [client, setClient] = useState(null);

  useEffect(() => {
    async function init() {
      // Exchange code for token
      const response = await request(
        `POST ${location.origin}/api/github/oauth/token`,
        {
          code,
        }
      );

      const client = initializeClient(response.data.token);
      setClient(client);
      setLoading(false);
    }

    init();
  }, []);

  if (loading) {
    return <h3>Loading...</h3>;
  }

  return <UserMe client={client} />;
};

export default ClientProvider;
